"use strict";angular.module("page.controllers").controller("PageCtrl",["$scope","$log","$window","$interval","$http","$modal","$location","$anchorScroll","pageService",function(e,n,s,t,i,a,o,c,r){e.pageService=r,r.autocomplete(),$("#recipients").on("beforeItemAdd",function(e){r.addContact(e).success(function(n){200!=n.meta.code&&($("#recipients").tagsinput("remove",e.item),alert("Invalid input: "+e.item+"\nPlease enter valid email or phone number."))})}),e.send_message=function(){$("#sending_gif").show(),r.sendMessage($("#recipients").tagsinput("items"),e.message).success(function(n){$("#sending_gif").hide(),200==n.meta.code?(e.clear(),alert("Your message was sent successfully.")):alert("Dude! Try sending your message later.")})},e.clear=function(){e.message="",$("#recipients").tagsinput("removeAll")}}]);